BEGIN TRANSACTION;
DROP TABLE IF EXISTS "Cities";
CREATE TABLE "Cities" (
	"cityID"	INTEGER,
	"name"	TEXT NOT NULL,
	"country"	TEXT NOT NULL,
	"country_code"	TEXT NOT NULL CHECK(LENGTH(country_code) = 2),
	"is_favourite"	INTEGER NOT NULL CHECK("is_favourite" BETWEEN 0 AND 1),
	PRIMARY KEY("cityID" AUTOINCREMENT)
);
DROP TABLE IF EXISTS "Weather";
CREATE TABLE "Weather" (
	"cityID"	INTEGER NOT NULL,
	"temp"	REAL NOT NULL,
	"feels_like"	REAL NOT NULL,
	"description"	TEXT NOT NULL,
	"humidity"	INTEGER NOT NULL CHECK("humidity" BETWEEN 0 AND 100),
	"wind_speed"	REAL NOT NULL,
	"offset"	INTEGER NOT NULL,
	"sunrise"	INTEGER NOT NULL,
	"sunset"	INTEGER NOT NULL,
	"tod"	VARCHAR(1) NOT NULL,
	PRIMARY KEY("cityID"),
	FOREIGN KEY("cityID") REFERENCES "Cities"("cityID") ON DELETE CASCADE
);
DROP INDEX IF EXISTS "cities_favourite_idx";
CREATE INDEX "cities_favourite_idx" ON "Cities" (
	"is_favourite"
);
DROP INDEX IF EXISTS "cities_name_idx";
CREATE INDEX "cities_name_idx" ON "Cities" ("name");
COMMIT;
